mapper.select:
SELECT *

FROM (SELECT ROWNUM AS rowno, r.*

FROM (SELECT *

FROM RECORD d WHERE 1=1

 AND d.FILENAME LIKE  '%' || #{record.fileName,jdbcType=VARCHAR} || '%'  
 AND d.TYPE = #{record.type,jdbcType=VARCHAR} 
 AND d.RESULT = #{record.result,jdbcType=VARCHAR} 
 AND d.DELDATE >= #{record.startDate,jdbcType=TIMESTAMP} 
 AND d.DELDATE < #{record.endDate,jdbcType=TIMESTAMP} 

) r

WHERE ROWNUM <= #{endRow,jdbcType=INTEGER}) t

WHERE t.rowno >= #{startRow,jdbcType=INTEGER}

SELECT count(seq)

FROM RECORD WHERE 1=1

 AND FILENAME LIKE  '%' || #{fileName,jdbcType=VARCHAR} || '%'  
 AND TYPE = #{type,jdbcType=VARCHAR} 
 AND RESULT = #{result,jdbcType=VARCHAR} 
 AND DELDATE >= #{startDate,jdbcType=TIMESTAMP} 
 AND DELDATE < #{endDate,jdbcType=TIMESTAMP}
