mapper.update:
UPDATE TIMER
type = #{type,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, cronExpression = #{cronExpression,jdbcType=VARCHAR}, filePath = #{filePath,jdbcType=VARCHAR}
WHERE seq = #{seq,jdbcType=NUMERIC}

mapper.select:
SELECT *

FROM (SELECT ROWNUM AS rowno, t.*

FROM (SELECT *

FROM TIMER WHERE 1=1

 AND NAME LIKE  '%' || #{name,jdbcType=VARCHAR} || '%'  
 AND TYPE = #{type,jdbcType=VARCHAR} 

) t

WHERE ROWNUM <= #{endRow,jdbcType=INTEGER}) table_alias

WHERE table_alias.rowno >= #{startRow,jdbcType=INTEGER}
